import React, { useState } from 'react'

const Contacts = ({countries, newSearch}) => {
    

    const countryToShow = newSearch === ''
    ? countries
    : countries.filter(country => country.name.includes(newSearch))

    const [showCountry, setCountry] = useState([])
    
    const Contact = ()=>{
      if (countryToShow.length === 1){
        return(
          <Display1 country = {countryToShow[0]}/>
        )
      }
      if (countryToShow.length > 10){
        return (
          <p>Too many matches, specify another fitler</p>
        )
      }
      return(
        <div>
          {countryToShow.map(c =>
            <div key={c.alpha2Code}>
              {c.name}
              <button onClick={() => setValue(c.name)}>
                show
              </button>
            </div>
          )}
        </div>
      )
    }

    const Display10 = ({country}) =>{
      console.log(country)
      return(
        <div>
          <p>{country.name}
          </p>
          <p><Display1 country = {country} state = {true}/></p>
        </div>    
      )}

    const Display1 = ({country, state}) =>{
      console.log(country)
        return(
          <div>
            <h1> {country.name} </h1>
            <p>capital {country.capital}</p>
            <p>population {country.population}</p>
            <DisplayLanguage languages = {country.languages} flag = {country.flag}/>
          </div>    
        )
      }

    const DisplayLanguage = ({languages, flag}) => {
      return(
        <div>
          <h2>languages</h2>
          <ul>
            {languages.map(language =>
              <li>{language.name}</li>
              )}
          </ul>
          <img src= {flag} height ="100px"/>
        </div>
      )
    }

    return (
      
      <div>
        <Contact/>
      </div>
    )
  }

export default Contacts
