import React, { useState } from 'react'

const Contacts = ({countries, newSearch}) => {
    const countryToShow = newSearch === ''
    ? countries
    : countries.filter(country => country.name.includes(newSearch))

    const Contact = ()=>{
      if (countryToShow.length === 1){
        const [showCountry, setShowCountry] = useState(true)
        return(
          <Display1 country = {countryToShow[0]} state = {showCountry}/>
        )
      }
      if (countryToShow.length > 10){
        return (
          <p>Too many matches, specify another fitler</p>
        )
      }else{
        return(
          <div>
            {countryToShow.forEach(element => {
              (country =>
                  <Display10 country = {country} />
              )})}
          </div>
      )
      }
    }

    const Display10 = ({country}) =>{
      const [showCountry, setShowCountry] = useState(false)
      return(
        <div>
          <p>{country.name}
          <button onClick={() => setShowCountry(!showCountry)}> show </button>
          </p>
          <p><Display1 country = {country} state = {showCountry}/></p>
        </div>    
      )}

    const Display1 = ({country, state}) =>{
      if (state === true ){
        return(
          <div>
            <h1> {country.name} </h1>
            <p>capital {country.capital}</p>
            <p>population {country.population}</p>
            <DisplayLanguage languages = {country.languages} flag = {country.flag}/>
          </div>    
        )}
      return(
        <p></p>
      )}

    const DisplayLanguage = ({languages, flag}) => {
      return(
        <div>
          <h2>languages</h2>
          <ul>
            {languages.map(language =>
              <li>{language.name}</li>
              )}
          </ul>
          <img src= {flag} height ="100px"/>
        </div>
      )
    }

    return (
      
      <div>
        <Contact/>
      </div>
    )
  }

export default Contacts
