import React, { useState } from 'react'

const Contacts = ({countries, newSearch}) => {
    

    const countryToShow = newSearch === ''
    ? countries
    : countries.filter(country => country.name.includes(newSearch))

    console.log(countryToShow)
    const items = []
    countryToShow.map(element => {
      items.push({
        name : element.name,
        show : false
      })
    })
    const [showCountry, setCountry] = useState([])
    
    console.log(items)
    console.log(showCountry)

    const Contact = ()=>{
      if (countryToShow.length === 1){
        return(
          <Display1 country = {countryToShow[0]} state = {true}/>
        )
      }
      if (countryToShow.length > 10){
        return (
          <p>Too many matches, specify another fitler</p>
        )
      }
      return(
          <div>
            {countryToShow.map(country1 => {
                  <p>{country1.name}</p>
            })}
          </div>
      )
    }
/*
    const showButton= (event ,{name}) => {
      setCountry(items)
      event.preventDefault()
      const index = items.findIndex(o => o[{name}])
      const state = !items[index]['show']
      const NewCopy = {
        name : {name},
        show : {state}
      }
      setCountry(items.splice(index, 1 , NewCopy))
    } 
    <button name = {country.name} onClick={() => showButton}> show </button>
    */

    const Display10 = ({country}) =>{
      return(
        <div>
          <p>{country.name}
          
          </p>
          <p><Display1 country = {country} state = {true}/></p>
        </div>    
      )}

    const Display1 = ({country, state}) =>{
      if (state === true ){
        return(
          <div>
            <h1> {country.name} </h1>
            <p>capital {country.capital}</p>
            <p>population {country.population}</p>
            <DisplayLanguage languages = {country.languages} flag = {country.flag}/>
          </div>    
        )}
      return(
        <p></p>
      )}

    const DisplayLanguage = ({languages, flag}) => {
      return(
        <div>
          <h2>languages</h2>
          <ul>
            {languages.map(language =>
              <li>{language.name}</li>
              )}
          </ul>
          <img src= {flag} height ="100px"/>
        </div>
      )
    }

    return (
      
      <div>
        <Contact/>
      </div>
    )
  }

export default Contacts
