import React from 'react'

const Contacts = ({countries, newSearch}) => {
    const countryToShow = newSearch === ''
    ? countries
    : countries.filter(country => country.name.includes(newSearch))

    const Contact = ()=>{
      if (countryToShow.length === 1){
        return(
          <Display1 country = {countryToShow[0]}/>
        )
      }
      if (countryToShow.length > 10){
        return (
          <p>Too many matches, specify another fitler</p>
        )
      }
      return(
        <div>
          {countryToShow.map(country =>
            <Display10 key = {country.numericCode} name ={country.name} />
            )}
        </div>
      )
      }

    const Display10 = ({name}) =>{
      return <p> {name} </p>
    }

    const Display1 = ({country}) =>{
      return(
        <div>
          <h1> {country.name} </h1>
          <p>capital {country.capital}</p>
          <p>population {country.population}</p>
          <DisplayLanguage languages = {country.languages} flag = {country.flag}/>

        </div>    
      )}

    const DisplayLanguage = ({languages, flag}) => {
      return(
        <div>
          <h2>languages</h2>
          <ul>
            {languages.map(language =>
              <li>{language.name}</li>
              )}
          </ul>
          <img src= {flag} height ="200px"/>
        </div>
      )
    }

    return (
      
      <div>
        <Contact/>
      </div>
    )
  }

export default Contacts
