import React, { useState } from 'react'

const Filter = ({country, newSearch, setSearch}) => {
  const handleSearch = (event) => {
    console.log(event.target.value)
    setSearch(event.target.value)
  }

  const CountryToShow = newSearch === ''
    ? country
    : country.filter(country => country.name.includes(newSearch))

  const Contact = ({name})=>{
    return(<p>{name}</p>
      )}


    return (
      <div> filter shown with: <input  value = {newSearch} onChange = {handleSearch}/> 
      <p>
        {CountryToShow.map(country =>
        <Contact key = {country.numericCode} name = {country.name}/>
      )} </p>
      </div>
    )
  }

export default Filter
